			ShapelessRecipeBuilder.shapeless(RecipeCategory.MISC, (ItemLike)((DeferredHolder)((Map)BlockInit.DYED_BLOCKS.get(color.getSerializedName())).get("concrete_powder")).get(), 8).group("concrete_powder").unlockedBy(getHasName(Items.SAND), has(Items.SAND)).unlockedBy(getHasName(Items.GRAVEL), has(Items.GRAVEL)).unlockedBy("has_dye", has(dyeTag)).requires(dyeTag).requires(Items.SAND).requires(Items.SAND).requires(Items.SAND).requires(Items.SAND).requires(Items.GRAVEL).requires(Items.GRAVEL).requires(Items.GRAVEL).requires(Items.GRAVEL).save(pRecipeOutput, ResourceLocation.fromNamespaceAndPath("delicate_dyes_tresified", color.getSerializedName() + "_concrete_powder"));
